svg.script:
var svgNS   = "http://www.w3.org/2000/svg";
var xlinkNS = "http://www.w3.org/1999/xlink";
var evNS    = "http://www.w3.org/2001/xml-events";

var labelit = "Magic";

function init() {
  function highlight() {
    var mycolor = "red";
    var texts = document.getElementsByTagName("text");
    for (var i=0;i<texts.length;i++) {
      var bbox = texts[i].getBBox();
      var ctm = texts[i].getScreenCTM();
      var mbox = document.createElementNS(svgNS,"rect");
      mbox.setAttributeNS(null,"x",bbox.x+ctm.e);
      mbox.setAttributeNS(null,"y",bbox.y+ctm.f);
      mbox.setAttributeNS(null,"width",bbox.width);
      mbox.setAttributeNS(null,"height",bbox.height);
      mbox.setAttributeNS(null,"fill","none");
      mbox.setAttributeNS(null,"stroke",mycolor);
      mbox.setAttributeNS(null,"stroke-dasharray","2,2");
      document.getElementById("theDesktop").appendChild(mbox);
      mycolor ="red";
    }
  }

  var button = drawPushButton({
    x: 110.5,
    y: 200.5,
    width: 64,
    height: 22,
    isDefault: true,
    label: labelit,
    hasFocus: true
  });
  document.getElementById("theDesktop").appendChild(button);

  button = drawRadioButton({
    x: 180.5,
    y: 200.5,
    label: labelit,
    indeterminate: false,
    checked: false
  });
  document.getElementById("theDesktop").appendChild(button);

  button = drawCheckButton({
    x: 240.5,
    y: 200.5,
    label: labelit,
    indeterminate: false,
    checked: true
  });
  document.getElementById("theDesktop").appendChild(button);
  button = drawOptionMenu({
    x: 300.5,
    y: 200.5,
    defaultOption: 2,
    options: ["Magic A","Magical B","Magically C"]
  });
  document.getElementById("theDesktop").appendChild(button);
  button = drawSpinField({
    x: 400.5,
    y: 200.5,
    width: 40
  });
  document.getElementById("theDesktop").appendChild(button);
  button = drawDetailsButton({x:450.5, y:200.5, label:labelit});
  document.getElementById("theDesktop").appendChild(button);

}
